var snd = new Audio("data:audio/wav;base64,"); 

function refresh_konsul()
{
    $.ajax({
        url: base_url + 'konsul/listbelumjawab',
        data: {},
        dataType: 'json',
        type: 'post',
        success: function(response){
            var list_unread = '';
            var tgl_daftar_disp = '';
            var tgl_daftar = '';
            
            var tgl = '';
            var bln = '';
            var thn = '';
            
            if (response.length) {
                for (idx in response) {
                    tgl_daftar = response[idx].tgl_daftar;
                    
                    thn = tgl_daftar.substr(0, 4);
                    bln = tgl_daftar.substr(5, 2);
                    tgl = tgl_daftar.substr(8, 2);
                    
                    jam = tgl_daftar.substr(11, 2);
                    mnt = tgl_daftar.substr(14, 2);
                    
                    list_unread = list_unread + '<li>' +
                            '<a target="_blank" href="' + base_url + 'konsul/lihat/' + response[idx].no_daftar + '">' +
                                '<div>' +
                                    '<strong><span class="label label-success">' + response[idx].nm_dokter + '</span></strong>' +
                                    '<span class="pull-right text-muted">' +
                                        '<em><small>' + tgl + '-' + bln + '-' + thn + ' ' + jam + ':' + mnt + '</small></em>' +
                                    '</span>' +
                                '</div>' +
                                '<div>' + 
                                    '<div>' + 
                                        '<small><strong>Pasien:</strong> ' + response[idx].nama_pasien + ' (<strong>' + response[idx].no_rm + '</strong>)' + '</small>' + 
                                    '</div>' + 
                                    '<div>' + 
                                        '<small><strong>Diagnosis:</strong> ' + response[idx].kons_diagnosis + '</small>' + 
                                    '</div>' + 
                                    '<div>' + 
                                        '<small><strong>Rencana:</strong> ' + response[idx].kons_rencana + '</small> ' + 
                                    '</div>' + 
                                '</div>' +
                            '</a>' +
                        '</li>' +
                        '<li class="divider"></li>';
                }
                $('.list-unread-konsul').html(list_unread);
                if (response.length > 3) {
                    $('.list-unread-konsul').css('height', '300px');
                    $('.list-unread-konsul').css('overflow-y', 'scroll');
                }
                $('.list-unread-konsul').css('width', '400px');
                $('.jumlah-konsul').html('(<strong>' + response.length + '</strong>)');
                $('[data-toggle="popover"]').popover('show');
                snd.play();
            } else {
                list_unread = list_unread + '<li>' +
                        '<a href="#" class="text-center">' +
                            'Tidak ada konsultasi' +
                        '</a>' +
                    '</li>';
                $('.list-unread-konsul').html(list_unread);
                $('.list-unread-konsul').css('height', '');
                $('.list-unread-konsul').css('width', '310px');
                $('.list-unread-konsul').css('overflow-y', '');
                $('.jumlah-konsul').html('');
            }
        },
        error: function(){
            
        }
    });
}

$('[data-toggle="popover"]').popover({
    placement : 'bottom',
    html : true,
    title : '<strong>Konsultasi Baru</strong> <a href="#" class="close" data-dismiss="alert"><span class="fa fa-remove"></span></a>',
    content : '<p><small>Anda mendapatkan konsultasi baru</small></p>'
});
$(document).on("click", ".popover .close" , function(){
    $(this).parents(".popover").popover('hide');
});

refresh_konsul();

setInterval(refresh_konsul, 60000);